/* Procedure structure for procedure `proc_comment_iud` */

/*!50003 DROP PROCEDURE IF EXISTS  `proc_comment_iud` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`lokesh`@`%` PROCEDURE `proc_comment_iud`(
p_mode TINYINT,
p_userId INT(11),
p_contentId INT(11),
p_comments varchar(255)
/*
call proc_comment_iud(3,null,3,null)
*/
)
BEGIN
 
   
       
       IF p_mode =0
    THEN
    INSERT INTO `tbl_comment`
     (contentId,`comments`,userId)
     VALUES(p_contentId,p_comments,p_userId);
     
     UPDATE `tbl_content` SET `comments`=
     (SELECT COUNT(*) FROM `tbl_comment` WHERE deleted='No' AND contentId=p_contentId) WHERE contentId=p_contentId;
     SELECT '200' responseCode,'data inserted successfully' responseMessage;
    
  END IF;
   /***View Start***/ 
      IF p_mode=3
    THEN 
        
        select  t2.`firstName`,t2.`lastName`,commentId,comments
         ,CASE WHEN HOUR(TIMEDIFF(NOW(),t1.createdDate)) >= 24
	THEN
	CONCAT(DATEDIFF(NOW(),t1.createdDate),' day ago')
	WHEN HOUR(TIMEDIFF(NOW(),createdDate)) < 24 AND HOUR(TIMEDIFF(NOW(),t1.createdDate)) >=1
	THEN
	CONCAT(HOUR(TIMEDIFF(NOW(),t1.createdDate)),' hour ago')
	WHEN HOUR(TIMEDIFF(NOW(),t1.createdDate)) = 00 
	AND MINUTE(TIMEDIFF(NOW(),t1.createdDate)) <= 59
	AND MINUTE(TIMEDIFF(NOW(),t1.createdDate)) > 00
	THEN
	CONCAT(MINUTE(TIMEDIFF(NOW(),t1.createdDate)),' min ago')
	ELSE
	'Just Now'
	END
	`dateTime` FROM  tbl_comment t1
	 left join `tbl_user` t2 on t1.`userId` = t2.`userId`
	WHERE  t1.deleted = 'No'
	AND t1.contentId=p_contentId;	
    END IF; 
    END */$$
DELIMITER ;

