/*!50003 DROP PROCEDURE IF EXISTS  `proc_like_iud` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`sanjiv`@`%` PROCEDURE `proc_like_iud`(
/*
Procedure Name - proc_like_iud
Purpose        - To insert, update, delete, view data in table- tbl_like
Author         - Shashank Chauhan
Date           - 21/10/2016
CALL proc_like_iud(0,'19','Y',103)
*/
p_mode TINYINT,
p_userId INT(11),
p_status VARCHAR(1),
p_contentId INT(11)
)
BEGIN
 
   
       IF p_mode =0
    THEN
    IF EXISTS(SELECT * FROM tbl_like WHERE userId=p_userId AND contentId=p_contentId)
    THEN 
    UPDATE tbl_like
    SET `status`=p_status,userId=p_userId,views=ifnull(views,0)+1
    WHERE userId=p_userId AND contentId=p_contentId;
    SELECT '200' responseCode, CASE WHEN p_status='Y' THEN 'Liked Successfully'
					ELSE 'Disliked Successfully'END responseMessage;
   update tbl_content
   set likes=case when p_status='Y' then ifnull(likes,0)+1
   else ifnull(likes,0)-1 end 
   where contentId=p_contentId;
